sourcesContent:
import mongoose from 'mongoose'

const ProductSchema = new mongoose.Schema(
    {
        title: {
            type: String,
            trim: true,
            required: [true, '   '],
            unique: true,
            maxlength: [40, '     40 '],
            minlength: [5, '     40 ']

        },
        description: {
            type: String,
            required: true,
            maxlength: [500, '     500 '],
        },
        price: {
            type: Number,
            required: true,
        },
        date: {
            type: Date,
            select: false,
            default: Date.now()
        },
        isMix: {
            type:Boolean,
            default: false
        },
        category:{
            type:mongoose.Schema.ObjectId,
            ref:'Categories'
        },
        mixes: [
            {
                separateId: {
                    type:mongoose.Schema.ObjectId,
                    ref:'Products'
                },
                percent: {
                    type:String
                }
            }
        ],
        image: String

    },
    {
        versionKey: false
    }


);

ProductSchema.pre(/^find/, function (next) {

   this.populate({
       path:'category',
       select: '-__v -description -image',

   });
   next()

});

module.exports = mongoose.model('Products', ProductSchema);

