import mongoose from 'mongoose'

const CategorySchema = new mongoose.Schema(
    {
        name: {
            type:String,
            required: [true, '   '],
            unique:true,
            maxlength:[40, '     40 '],
            minlength:[5, '     5 ']
        },
        description: {
            type:String,
            required: true,
            maxlength:[500, '     500 '],
        },
        image: {
            type:String
        }
    }
);

module.exports = mongoose.model('Categories', CategorySchema);
